function generarHTMLVulnerabilidad(vuln) {
  let cvssColor;
  let cvssBackColor;
  let cvssValue;

  // Manejar el caso especial de CVSS "TBD"
  if (vuln.cvss === "TBD" || vuln.cvss === null || vuln.cvss === undefined) {
    cvssColor = '#ff9933'; // Naranja
    cvssBackColor = '#fff5e6';
    cvssValue = "Pendiente";
  } else {
    // Manejar valores num칠ricos normales
    if (vuln.cvss >= 7) {
      cvssColor = '#dc3545';
      cvssBackColor = '#ffeaea';
    } else if (vuln.cvss >= 4) {
      cvssColor = '#ff9933';
      cvssBackColor = '#fff5e6';
    } else {
      cvssColor = '#28a745';
      cvssBackColor = '#e6f5e6';
    }
    cvssValue = vuln.cvss;
  }

  // Generar HTML para acciones recomendadas
  let accionesHTML = '';
  if (vuln.acciones_recomendadas && vuln.acciones_recomendadas.length > 0) {
    accionesHTML = vuln.acciones_recomendadas.map(accion => 
      `<li style="margin-bottom: 5px;">${accion}</li>`
    ).join('');
  }

  // Generar HTML para referencias
  let referenciasHTML = '';
  if (vuln.referencias && vuln.referencias.length > 0) {
    referenciasHTML = vuln.referencias.map(ref => 
      `<a href="${ref}" style="color: #2c67b2; font-size: 13px; word-break: break-all; display: block; margin-bottom: 5px;">${ref}</a>`
    ).join('');
  }

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="margin-bottom: 20px; border: 1px solid #eaeaea; border-radius: 5px;">
    <tr>
      <td style="padding: 15px 20px;">
        <table width="100%" border="0" cellpadding="0" cellspacing="0">
          <tr>
            <td>
              <div style="font-weight: bold; font-size: 18px; color: #2c67b2; margin-bottom: 8px;">${vuln.fabricante || 'Fabricante no especificado'}</div>
              <div style="font-size: 14px; color: #666; margin-top: 5px;">
                <span><strong>CVE:</strong> ${vuln.cve}</span><br>
                <span><strong>Fecha del Reporte:</strong> ${vuln.fecha_reporte}</span><br>
                <span><strong>Producto Afectado:</strong> ${vuln.producto}</span><br>
                <span style="display: inline-block; padding: 3px 8px; border-radius: 3px; font-weight: bold; font-size: 12px; margin-top: 5px; background-color: ${cvssBackColor}; color: ${cvssColor};">CVSS: ${cvssValue}</span>
              </div>
            </td>
          </tr>
          <tr>
            <td style="padding-top: 15px;">
              <table width="100%" border="0" cellpadding="12" cellspacing="0" style="background-color: #f8f9fa; border-radius: 5px; margin: 10px 0;">
                <tr>
                  <td>
                    <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Descripci칩n T칠cnica</div>
                    <p>${vuln.descripcion}</p>
                  </td>
                </tr>
              </table>
            </td>
          </tr>
          <tr>
            <td style="padding-top: 15px;">
              <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Acciones Recomendadas</div>
              <ul style="padding-left: 25px; margin-bottom: 15px;">
                ${accionesHTML}
              </ul>
            </td>
          </tr>
          <tr>
            <td>
              <div style="font-weight: bold; margin-bottom: 5px; color: #555;">Referencias</div>
              ${referenciasHTML}
            </td>
          </tr>
        </table>
      </td>
    </tr>
  </table>`;
}

