const formatISO8601 = (date) => date.toISOString().replace(/\.\d{3}Z$/, '.000Z');
return {
  startDate: formatISO8601(startOfDay),
  endDate: formatISO8601(endOfDay),
  apikey: apiKey
};

const now = new Date();
const endDate = new Date(Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate(), 23, 59, 59));
const startDate = new Date(endDate);
startDate.setUTCDate(startDate.getUTCDate() - 7); // resta 7 d칤as (una semana)
startDate.setUTCHours(0, 0, 0, 0); // establece al inicio del d칤a

const formatISO8601 = (date) => date.toISOString().replace(/\.\d{3}Z$/, '.000Z');

return {
  startDate: formatISO8601(startDate),
  endDate: formatISO8601(endDate)
};

