// Function node  n8n
// Toma la salida LLM en item.json.output (string con ```json ... ``` o JSON) y genera htmlEmail + summaryJson + conteos

// -------------------- Helpers --------------------
function cleanJsonString(str) {
 if (!str || typeof str !== 'string') return null;
 // Extraer contenido entre ```json ... ``` o entre ``` ... ```
 const mdJson = str.match(/```(?:json)?\s*([\s\S]*?)\s*```/i);
 if (mdJson && mdJson[1]) return mdJson[1].trim();
 // Si no hay fences, devolver la cadena limpia
 return str.trim();
}

function formatearPeriodo(startDate, endDate) {
 if (!startDate || !endDate) return "";
 const meses = ['Enero', 'Febrero', 'Marzo', 'Abril', 'Mayo', 'Junio', 'Julio', 'Agosto', 'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'];
 const inicio = new Date(startDate);
 const fin = new Date(endDate);
 if (inicio.toDateString() === fin.toDateString()) {
  return `${inicio.getDate()} de ${meses[inicio.getMonth()]} de ${inicio.getFullYear()}`;
 } else {
  return `${inicio.getDate()} de ${meses[inicio.getMonth()]} al ${fin.getDate()} de ${meses[fin.getMonth()]} de ${fin.getFullYear()}`;
 }
}

function createEmailSafeHeader(date, startDate, endDate) {
  const periodText = formatearPeriodo(startDate, endDate);
  const primaryColor = "#1a237e";
  const bg = "https://media.licdn.com/dms/image/v2/D4E3DAQFqcEmbz29Nmw/image-scale_191_1128/image-scale_191_1128/0/1695664141867/ifx_networks_cover?e=2147483647&v=beta&t=JVG-v8jzdy-D85rsPWHrfn_rzAnV9459-s6uzEw-bPM";
  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="background-color: ${primaryColor}; background-image: url('${bg}'); background-size: cover; background-position: right center; margin-bottom: 20px;">
    <tr>
      <td align="left" valign="middle" width="120" style="padding: 15px;">
        <img src="https://github.com/aifx-dev/repo-images/blob/main/Logo_aifx_blanco_circle.png?raw=true" alt="Logo" style="width: 110px; height: auto;">
      </td>
      <td align="left" valign="middle" style="padding: 15px;">
        <h1 style="color: #ffffff; font-size: 28px; font-weight: 800; margin: 0; text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.5);">BOLET칈N DE CIBERSEGURIDAD</h1>
        ${periodText ? `<p style="color: #ffffff; font-size: 14px; margin: 5px 0 0 0; text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);">Periodo de Consulta: ${periodText}</p>` : ''}
      </td>
    </tr>
    <tr>
      <td colspan="2" align="right" style="padding-right: 15px; padding-bottom: 5px;">
        <p style="color: #ffffff; font-size: 11px; margin: 0;">Generated by <i style="font-style: italic;">aifx</i></p>
      </td>
    </tr>
  </table>
  `;
}

function renderStatsFromCounts(counts) {
  const alta = counts.alta || 0;
  const media = counts.media || 0;
  const baja = counts.baja || 0;
  const total = alta + media + baja;

  const cardStyle = `background-color: #f8f9fa; border-radius: 8px; border: 1px solid #e0e0e0; padding: 15px; text-align: center; font-family: Segoe UI, Roboto, sans-serif;`;
  const valueStyle = `font-size: 28px; font-weight: 700; margin-bottom: 5px;`;
  const labelStyle = `font-size: 14px; color: #6c757d;`;

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="10" style="margin-bottom:20px;">
    <tr>
      <td valign="top" style="width: 25%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}; color: #dc3545;">${alta}</div>
          <div style="${labelStyle}">Vulnerabilidades ALTA</div>
        </div>
      </td>
      <td valign="top" style="width: 25%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}; color: #ff9933;">${media}</div>
          <div style="${labelStyle}">Vulnerabilidades MEDIA</div>
        </div>
      </td>
      <td valign="top" style="width: 25%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}; color: #28a745;">${baja}</div>
          <div style="${labelStyle}">Vulnerabilidades BAJA</div>
        </div>
      </td>
      <td valign="top" style="width: 25%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}; color: #2c67b2;">${total}</div>
          <div style="${labelStyle}">Total</div>
        </div>
      </td>
    </tr>
  </table>`;
}

function generarHTMLVulnerabilidad(vuln) {
  let cvssVal = vuln.cvss;
  if (cvssVal && typeof cvssVal === 'object' && 'score' in cvssVal) cvssVal = cvssVal.score;
  if (cvssVal === null || cvssVal === undefined || cvssVal === 'TBD') cvssVal = "Pendiente";
  const cvssNum = typeof cvssVal === 'number' ? cvssVal : null;
  let cvssColor, cvssBack;

  if (cvssNum !== null) {
    if (cvssNum >= 7) { cvssColor = '#dc3545'; cvssBack = '#ffeaea'; }
    else if (cvssNum >= 4) { cvssColor = '#ff9933'; cvssBack = '#fff5e6'; }
    else { cvssColor = '#28a745'; cvssBack = '#e6f5e6'; }
  } else {
    cvssColor = '#ff9933'; cvssBack = '#fff5e6';
  }

  const accionesHTML = (vuln.acciones_recomendadas || []).map(a => `<li style="margin-bottom:5px;">${a}</li>`).join('');
  const referenciasHTML = (vuln.referencias || []).map(r => `<a href="${r}" style="color:#2c67b2;display:block;word-break:break-all;margin-bottom:5px;" target="_blank" rel="noopener noreferrer">${r}</a>`).join('');

  return `
  <div style="margin-bottom:20px;border:1px solid #eaeaea;border-radius:8px;padding:20px;background:#fff;">
    <div style="font-weight:700;font-size:18px;color:#2c67b2;margin-bottom:8px;">${vuln.fabricante || 'Fabricante no especificado'}</div>
    <div style="font-size:14px;color:#555;">
      <div><strong>CVE:</strong> ${vuln.cve || 'Sin CVE'}</div>
      <div><strong>Producto Afectado:</strong> ${vuln.producto || 'No especificado'}</div>
      <div style="display:inline-block;padding:4px 10px;border-radius:5px;font-weight:700;font-size:12px;margin-top:8px;background-color:${cvssBack};color:${cvssColor};">CVSS: ${cvssVal}</div>
    </div>
    <div style="margin-top:15px;color:#444;">
      <div style="font-weight:700;margin-bottom:8px;">Descripci칩n t칠cnica</div>
      <p style="margin:0;font-size:14px;">${vuln.descripcion || 'No disponible'}</p>
    </div>
    <div style="margin-top:15px;">
      <div style="font-weight:700;margin-bottom:8px;">Acciones recomendadas</div>
      <ul style="padding-left:20px;margin:0;">${accionesHTML || '<div style="color:#666;font-style:italic;">No especificadas</div>'}</ul>
    </div>
    <div style="margin-top:15px;">
      <div style="font-weight:700;margin-bottom:8px;">Referencias</div>
      <div style="font-size:14px;">${referenciasHTML || '<div style="color:#666;font-style:italic;">No hay referencias</div>'}</div>
    </div>
  </div>`;
}

// -------------------- Procesamiento principal --------------------
const items = $input.all();
const returnItems = [];

for (const item of items) {
  try {
    const rawOutput = item.json.output;
    if (!rawOutput) throw new Error('No se encontr칩 campo output en el item');

    const cleaned = cleanJsonString(rawOutput);
    if (!cleaned) throw new Error('No se pudo limpiar el campo output');

    let parsed;
    try {
      parsed = JSON.parse(cleaned);
    } catch (e) {
      throw new Error('JSON inv치lido despu칠s de limpiar el output: ' + e.message);
    }

    const doc = parsed;
    if (!doc.secciones || !Array.isArray(doc.secciones)) {
      throw new Error('El JSON no contiene "secciones" como array');
    }

    let consolidated = doc.consolidado || null;
    if (!consolidated) {
      let total = 0, alta = 0, media = 0, baja = 0, cvssSum = 0, cvssCount = 0;
      for (const sec of doc.secciones) {
        if (sec.vulnerabilidades && Array.isArray(sec.vulnerabilidades)) {
          total += sec.vulnerabilidades.length;
          for (const v of sec.vulnerabilidades) {
            const c = String(v.criticidad || '').toUpperCase();
            if (c === 'ALTA') alta++;
            else if (c === 'MEDIA') media++;
            else if (c === 'BAJA') baja++;
            let sc = v.cvss;
            if (sc && typeof sc === 'object' && 'score' in sc) sc = sc.score;
            if (typeof sc === 'number' && !isNaN(sc)) { cvssSum += sc; cvssCount++; }
          }
        }
      }
      consolidated = {
        total_vulnerabilidades: total,
        vulnerabilidades_altas: alta,
        vulnerabilidades_medias: media,
        vulnerabilidades_bajas: baja,
        promedio_cvss: cvssCount > 0 ? Math.round((cvssSum / cvssCount) * 100) / 100 : null
      };
    }

    let conteoAltas = 0;
    const referenciasSet = new Set();
    for (const sec of doc.secciones) {
      if (sec.vulnerabilidades && Array.isArray(sec.vulnerabilidades)) {
        for (const v of sec.vulnerabilidades) {
          if (String(v.criticidad || '').toUpperCase() === 'ALTA') conteoAltas++;
          if (v.referencias && Array.isArray(v.referencias)) {
            for (const r of v.referencias) {
              if (r) referenciasSet.add(r);
            }
          }
        }
      }
    }
    const conteoReferencias = referenciasSet.size;

    const ahora = new Date();
    const meses = ['Enero','Febrero','Marzo','Abril','Mayo','Junio','Julio','Agosto','Septiembre','Octubre','Noviembre','Diciembre'];
    const fechaFormateada = `${meses[ahora.getMonth()]} ${ahora.getDate()} de ${ahora.getFullYear()}`;
    const startDate = item.json.startDate || null;
    const endDate = item.json.endDate || null;

    const headerHtml = createEmailSafeHeader(fechaFormateada, startDate, endDate);
    const statsHtml = renderStatsFromCounts({
      alta: consolidated.vulnerabilidades_altas || 0,
      media: consolidated.vulnerabilidades_medias || 0,
      baja: consolidated.vulnerabilidades_bajas || 0
    });

    let bodyHtml = `
      <div style="padding:30px;">
        <div style="background-color:#f1f5f9;border-left:4px solid #2c67b2;padding:12px 20px;margin-bottom:25px;font-size:14px;border-radius:6px;">
          <strong>FUENTE DEL REPORTE:</strong> ${doc.metadata && doc.metadata.fuente ? doc.metadata.fuente : 'Consolidado'}
        </div>
        ${statsHtml}
        <div style="font-size:13px;color:#666;text-align:center;margin-bottom:20px;">
          <strong>Referencias 칰nicas:</strong> ${conteoReferencias} &nbsp;  &nbsp; <strong>Vulnerabilidades ALTA (global):</strong> ${conteoAltas}
        </div>
    `;

    for (const sec of doc.secciones) {
      let secTitle = sec.title || sec.id || 'Secci칩n';
      secTitle = secTitle.replace(/\bNVI\b/gi, 'NVD');

      const rawFuente = sec.metadata && sec.metadata.fuente ? sec.metadata.fuente : '';
      let secFuente = rawFuente;
      if (/nvi/i.test(sec.id || '') || /nvi/i.test(sec.title || '') || /national vulnerability database/i.test(rawFuente) || /nvd/i.test(rawFuente)) {
        secFuente = 'NVD (National Vulnerability Database) NIST.GOV';
      } else if (/vulners/i.test(rawFuente) || /vulners/i.test(sec.title || '') || /soc diario vulners/i.test((sec.title||'').toLowerCase())) {
        secFuente = 'Vulners.com';
      } else if (!rawFuente) {
        secFuente = '';
      }

      const secFecha = sec.metadata && sec.metadata.fecha ? sec.metadata.fecha : '';

      bodyHtml += `
        <div style="margin-bottom:20px;">
          <h2 style="font-size:24px;font-weight:700;color:#123c69;border-bottom:2px solid #0071ce;padding-bottom:5px;margin:25px 0 20px 0;">
            ${secTitle}
            ${secFuente || secFecha ? `<span style="font-size:15px;color:#666;font-weight:400;margin-left:15px;">${secFuente}${secFuente && secFecha ? '  ' : ''}${secFecha}</span>` : ''}
          </h2>
      `;

      if (sec.vulnerabilidades && sec.vulnerabilidades.length > 0) {
        for (const v of sec.vulnerabilidades) {
          bodyHtml += generarHTMLVulnerabilidad(v);
        }
      } else {
        bodyHtml += `<div style="background:#f8f9fa;padding:15px;border-radius:6px;color:#666;font-style:italic;">No se reportaron vulnerabilidades en esta secci칩n.</div>`;
      }

      if (sec.pautas_generales && sec.pautas_generales.length > 0) {
        bodyHtml += `
          <div style="background:#f1f5f9;border-radius:6px;padding:15px;margin-top:15px;">
            <div style="font-weight:700;margin-bottom:8px;">Pautas generales</div>
            <ul style="margin:0;padding-left:20px;">
              ${sec.pautas_generales.map(p => `<li style="margin-bottom:6px;">${p}</li>`).join('')}
            </ul>
          </div>
        `;
      }

      bodyHtml += `</div><hr style="border:0;height:1px;background:#eee;margin:25px 0;">`;
    }

    bodyHtml += `</div>`;

    const footerHtml = `
      <div style="background:#232a34;color:#e0e0e0;padding:24px 30px;font-size:13px;text-align:left;margin-top:20px;">
        <div style="font-weight:700;color:#fff;margin-bottom:4px;">IFX Networks</div>
        <div style="line-height:1.6;">
          25 a침os de experiencia en el mercado de las Telecomunicaciones.<br>
          Diagonal 97 No. 17  60 piso 4, Bogot치, Colombia<br>
          +57 (601) 369 3000  CARE: +57 (601) 369 3024
        </div>
      </div>
    `;

    const html = `<!DOCTYPE html>
<html lang="es">
<head>
  <meta charset="utf-8">
  <meta name="viewport" content="width=device-width,initial-scale=1">
  <title>Bolet칤n Consolidado</title>
  <style>
    body { font-family: Arial, Helvetica, sans-serif; background-color: #f4f6f8; margin: 0; color: #2c3e50; }
    .container { max-width: 800px; margin: 20px auto; background: #fff; border-radius: 8px; overflow: hidden; box-shadow: 0 4px 10px rgba(44,62,80,0.1); }
  </style>
</head>
<body>
  <div class="container">
    ${headerHtml}
    ${bodyHtml}
    ${footerHtml}
  </div>
  <script id="security-data" type="application/json" style="display:none;">${JSON.stringify(doc)}</script>
</body>
</html>`;

    const summaryJson = {
      metadata: doc.metadata || {},
      consolidado: consolidated,
      conteos: {
        conteo_vulnerabilidades_altas: conteoAltas,
        conteo_referencias: conteoReferencias
      },
      secciones: doc.secciones || []
    };

    returnItems.push({
      json: {
        htmlEmail: html,
        summaryJson,
        conteo_vulnerabilidades_altas: conteoAltas,
        conteo_referencias: conteoReferencias
      }
    });
  } catch (err) {
    returnItems.push({
      json: {
        error: err.message || String(err)
      }
    });
  }
}

return returnItems;
