  const bg = "https://media.licdn.com/dms/image/v2/D4E3DAQFqcEmbz29Nmw/image-scale_191_1128/image-scale_191_1128/0/1695664141867/ifx_networks_cover?e=2147483647&v=beta&t=JVG-v8jzdy-D85rsPWHrfn_rzAnV9459-s6uzEw-bPM";
  const primaryColor = "#1a237e";
  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="0" style="background-color: ${primaryColor}; background-image: url('${bg}'); background-size: cover; background-position: right center; margin-bottom: 20px;">
    <tr>
      <td align="left" valign="middle" width="120" style="padding: 15px;">
        <img src="https://github.com/aifx-dev/repo-images/blob/main/Logo_aifx_blanco_circle.png?raw=true" alt="Logo" style="width: 110px; height: auto;">
      </td>
      <td align="left" valign="middle" style="padding: 15px;">
        <h1 style="color: #ffffff; font-size: 28px; font-weight: 800; margin: 0; text-shadow: 1px 1px 3px rgba(0,0,0,0.5);">BOLET√çN DE CIBERSEGURIDAD</h1>
        ${periodText ? `<p style="color: #ffffff; font-size: 14px; margin: 5px 0 0 0; text-shadow: 1px 1px 2px rgba(0,0,0,0.5);">Periodo de Consulta: ${periodText}</p>` : ''}
        <p style="color:#ffffff;font-size:12px;margin:6px 0 0 0;text-shadow:1px 1px 2px rgba(0,0,0,0.5);">Fecha de emisi√≥n: <strong>${todayISO}</strong></p>
      </td>
    </tr>
    <tr>
      <td colspan="2" align="right" style="padding-right: 15px; padding-bottom: 5px;">
        <p style="color: #ffffff; font-size: 11px; margin: 0;">Generated by <i style="font-style: italic;">aifx</i></p>
      </td>
    </tr>
  </table>
  `;
