    <div style="line-height:1.6;">
      25 a√±os de experiencia en el mercado de las Telecomunicaciones.<br>
      Diagonal 97 No. 17 ‚Äì 60 piso 4, Bogot√°, Colombia<br>
      +57 (601) 369 3000 ‚Äî CARE: +57 (601) 369 3024
    </div>
  </div>`;
}

/* ===================== Markdown helpers ===================== */
function extractLinksFromMarkdown(markdown) {
  const links = [];
  const regex = /\[(.*?)\]\((.*?)\)/g;
  let match;
  while ((match = regex.exec(markdown)) !== null) {
    links.push(match[2]);
  }
  return links;
}

function extractMarkdown(rawOutput) {
  let markdown = '';
  if (typeof rawOutput === 'string') {
    const cleaned = rawOutput.replace(/^\s*```(?:json|js)?\s*/im, '').replace(/\s*```$/m, '').trim();
    try {
      const parsed = JSON.parse(cleaned);
      if (parsed && typeof parsed === 'object' && parsed.markdown) {
        markdown = parsed.markdown || '';
      } else {
        markdown = cleaned;
      }
    } catch (err) {
      markdown = cleaned;
    }
  } else if (rawOutput && typeof rawOutput === 'object') {
    markdown = rawOutput.markdown || '';
  }
  return markdown;
}

/* ===================== Try detect structured security data ===================== */
function tryParseSecurityData(rawOutput) {
  if (!rawOutput) return null;
  try {
    if (typeof rawOutput === 'string') {
      const s = rawOutput.trim();
      if (s.startsWith('{') || s.startsWith('[')) {
        const parsed = JSON.parse(s);
        if (parsed && parsed.vulnerabilidades && Array.isArray(parsed.vulnerabilidades)) return parsed;
        if (parsed && parsed.output) {
          try {
            const inner = typeof parsed.output === 'string' ? JSON.parse(parsed.output) : parsed.output;
            if (inner && inner.vulnerabilidades && Array.isArray(inner.vulnerabilidades)) return inner;
          } catch (e) {}
        }
      }
    } else if (typeof rawOutput === 'object') {
      if (rawOutput.vulnerabilidades && Array.isArray(rawOutput.vulnerabilidades)) return rawOutput;
    }
  } catch (e) {}
  return null;
}

/* ===================== New: contar elementos en segmentos espec√≠ficos del markdown ===================== */
function escapeRegex(str) {
  return str.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
}

/**
 * Busca el encabezado que contiene `segmentTitle` (ignorando niveles de #)
 * y cuenta los elementos de lista (-, *, n√∫meros) que aparezcan hasta el
 * siguiente encabezado o bloque de secci√≥n.
 * Devuelve 0 si no encuentra el segmento.
 */
function countItemsInSegment(markdown, segmentTitle) {
  if (!markdown || !segmentTitle) return 0;
  const lines = markdown.split(/\r?\n/);
  const segLower = segmentTitle.trim().toLowerCase();

  let startIndex = -1;
  for (let i = 0; i < lines.length; i++) {
    const raw = lines[i].trim();
    const noHash = raw.replace(/^#+\s*/, '').trim();
    if (!noHash) continue;
    if (noHash.toLowerCase().startsWith(segLower)) {
      startIndex = i;
      break;
    }
    if (raw.toLowerCase().startsWith(segLower)) {
      startIndex = i;
      break;
    }
  }

  if (startIndex === -1) return 0;

  let count = 0;
  for (let j = startIndex + 1; j < lines.length; j++) {
    const line = lines[j];
    const trimmed = line.trim();
    if (trimmed === '') {
      continue;
    }
    if (/^\s*#{1,6}\s+/.test(line)) break;
    if (/^[^\s].*:$/.test(trimmed) && !/^(?:-|\*|\d+\.)\s+/.test(trimmed)) break;
    if (/^\s*(?:-|\*|\+|\d+\.)\s+/.test(line) || /^\s*[\u2022\u2023\u25E6]\s+/.test(line)) {
      count++;
      continue;
    }
    if (/^\s*[\p{Emoji_Presentation}\u{1F300}-\u{1FAFF}]/u.test(trimmed) && trimmed.length < 120) {
      count++;
      continue;
    }
    if (/^[-*_]{3,}$/.test(trimmed)) break;
  }

  return count;
}

/* ===================== Compute header indicators using the segments ===================== */
function computeHeaderIndicators(markdown, securityData) {
  const refTitle = 'üåê Referencia de donde viene la informaci√≥n';
  const altaTitle = 'üö® Nivel de Alerta General:';

  const refsFromSegment = countItemsInSegment(markdown, refTitle);
  const altaFromSegment = countItemsInSegment(markdown, altaTitle);

  const refsFallback = extractLinksFromMarkdown(markdown).length;
  let altaFallback = 0;
  if (securityData && Array.isArray(securityData.vulnerabilidades)) {
    altaFallback = securityData.vulnerabilidades.reduce((acc, v) => {
      const c = (v.criticidad || '').toString().toUpperCase();
      return acc + (c === 'ALTA' ? 1 : 0);
    }, 0);
  } else {
    altaFallback = (markdown.match(/\bALTA\b/gi) || []).length;
  }

  const references = refsFromSegment > 0 ? refsFromSegment : refsFallback;
  const alta = altaFromSegment > 0 ? altaFromSegment : altaFallback;

  return { references, alta };
}

/* Render header indicators */
function renderHeaderIndicators(ind) {
  const refs = ind.references;
  const alta = ind.alta;
  const cardStyle = `background-color: #f8f9fa; border-radius: 8px; border: 1px solid #e0e0e0; padding: 15px; text-align: center; font-family: Segoe UI, Roboto, sans-serif;`;
  const valueStyle = `font-size: 28px; font-weight: 700; color: #0071ce; margin-bottom: 5px;`;
  const labelStyle = `font-size: 14px; color: #6c757d;`;

  return `
  <table width="100%" border="0" cellpadding="0" cellspacing="10" style="margin-bottom:20px;">
    <tr>
      <td valign="top" style="width: 50%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}">${refs}</div>
          <div style="${labelStyle}">üåê Referencias</div>
        </div>
      </td>
      <td valign="top" style="width: 50%;">
        <div style="${cardStyle}">
          <div style="${valueStyle}; color: #dc3545;">${alta}</div>
          <div style="${labelStyle}">üö® Vulnerabilidades (ALTA)</div>
        </div>
      </td>
    </tr>
  </table>`;
}

/* ===================== Render security section (omit Fecha del Reporte) ===================== */
function escapeHtml(str) {
  return String(str || '').replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#39;');
}

function renderSecuritySection(securityData) {
  if (!securityData || !Array.isArray(securityData.vulnerabilidades)) return '';

  const buckets = { ALTA: [], MEDIA: [], BAJA: [] };
  securityData.vulnerabilidades.forEach(v => {
    const c = (v.criticidad || '').toString().toUpperCase();
    if (c === 'ALTA') buckets.ALTA.push(v);
    else if (c === 'MEDIA') buckets.MEDIA.push(v);
    else buckets.BAJA.push(v);
  });

  let html = `<div style="margin-top:18px;">`;

  ['ALTA', 'MEDIA', 'BAJA'].forEach(level => {
    const arr = buckets[level];
    const color = level === 'ALTA' ? '#dc3545' : level === 'MEDIA' ? '#ff9933' : '#28a745';
    html += `
      <div style="margin-bottom:20px;">
        <h3 style="margin:0;font-size:18px;color:${color};font-weight:700;padding-bottom:5px;border-bottom:2px solid ${color};">
          Criticidad ${level} (${arr.length})
        </h3>`;

    if (arr.length === 0) {
      html += `<div style="background:#f8f9fa;padding:15px;border-radius:6px;color:#666;font-style:italic;">No se han detectado vulnerabilidades de este nivel en el dataset.</div>`;
    } else {
      arr.forEach(v => {
        const cvss = (v.cvss === null || v.cvss === undefined || v.cvss === 'TBD') ? 'Pendiente' : v.cvss;
        const cvssColor = cvss === 'Pendiente' ? '#ff9933' : (cvss >= 7 ? '#dc3545' : cvss >= 4 ? '#ff9933' : '#28a745');
        const cvssBgColor = cvss === 'Pendiente' ? '#fff5e6' : (cvss >= 7 ? '#ffeaea' : cvss >= 4 ? '#fff5e6' : '#e6f5e6');

        let accionesHTML = '';
        if (v.acciones_recomendadas && Array.isArray(v.acciones_recomendadas)) {
          accionesHTML = `<ul style="padding-left:20px;margin:6px 0;">${v.acciones_recomendadas.map(a => `<li>${escapeHtml(String(a))}</li>`).join('')}</ul>`;
        }
        let referenciasHTML = '';
        if (v.referencias && Array.isArray(v.referencias)) {
          referenciasHTML = `<div style="margin-top:6px;">${v.referencias.map(r => `<a href="${r}" style="color:#2c67b2;display:block;word-break:break-all;">${r}</a>`).join('')}</div>`;
        }

        html += `
          <div style="border:1px solid #eaeaea;border-radius:8px;padding:20px;margin-top:10px;background:#fff;">
            <div style="font-weight:700;font-size:15px;color:#2c67b2;margin-bottom:8px;">${escapeHtml(v.fabricante || 'Fabricante no especificado')}</div>
            <div style="font-size:14px;color:#555;">
              <div><strong>CVE:</strong> ${escapeHtml(v.cve || 'N/A')}</div>
              <div><strong>Producto afectado:</strong> ${escapeHtml(v.producto || 'N/A')}</div>
              <div style="display:inline-block;padding:4px 10px;border-radius:5px;margin-top:8px;background:${cvssBgColor};color:${cvssColor};font-weight:700;">CVSS: ${escapeHtml(String(cvss))}</div>
            </div>
            <div style="margin-top:15px;color:#444;">
              <div style="font-weight:700;margin-bottom:8px;">Descripci√≥n t√©cnica</div>
              <div style="font-size:14px;">${escapeHtml(v.descripcion || 'No disponible')}</div>
            </div>
            <div style="margin-top:15px;">
              <div style="font-weight:700;margin-bottom:8px;">Acciones recomendadas</div>
              <div style="font-size:14px;">${accionesHTML || '<div style="color:#666;font-style:italic;">No hay acciones recomendadas.</div>'}</div>
            </div>
            <div style="margin-top:10px;">
              <div style="font-weight:700;margin-bottom:8px;">Referencias</div>
              <div style="font-size:14px;">${referenciasHTML || '<div style="color:#666;font-style:italic;">No hay referencias.</div>'}</div>
            </div>
          </div>`;
      });
    }

    html += `</div>`;
  });

  html += `</div>`;
  return html;
}

/* ===================== Embellecedor ‚ÄúAn√°lisis Consolidado‚Äù ===================== */
function prettifyConsolidatedSection(md) {
  if (!md || typeof md !== 'string') return md;

  // Normaliza espacios ‚Äúpegados‚Äù entre secciones enumeradas
  md = md.replace(/\s*(\n)?\s*(?=1\.\s*Actores recurrentes)/i, '\n\n');
  md = md.replace(/\s*(?=2\.\s*Vectores de ataque comunes)/i, '\n\n');
  md = md.replace(/\s*(?=3\.\s*Industrias afectadas)/i, '\n\n');
  md = md.replace(/\s*(?=4\.\s*Impacto general)/i, '\n\n');
  md = md.replace(/\s*(?=5\.\s*Tendencias destacadas)/i, '\n\n');

  // Convierte t√≠tulos enumerados en encabezados H3 con iconos
  md = md.replace(/^1\.\s*Actores recurrentes:?\s*$/gmi, '### üë• Actores recurrentes');
  md = md.replace(/^2\.\s*Vectores de ataque comunes:?\s*$/gmi, '### üõ†Ô∏è Vectores de ataque comunes');
  md = md.replace(/^3\.\s*Industrias afectadas:?\s*$/gmi, '### üè≠ Industrias afectadas');
  md = md.replace(/^4\.\s*Impacto general:?\s*$/gmi, '### üìâ Impacto general');
  md = md.replace(/^5\.\s*Tendencias destacadas:?\s*$/gmi, '### üìà Tendencias destacadas');

  // Si la l√≠nea ‚ÄúNo se identifican datos relevantes.‚Äù viene pegada al t√≠tulo, forzamos salto y vi√±eta
  md = md.replace(/(### [^\n]+)\s*-?\s*No se identifican datos relevantes\.?/g, '$1\n\n- ‚ö™ No se identifican datos relevantes');

  // Donde aparezca el literal gen√©rico, uniformar con el punto y el icono
  md = md.replace(/-?\s*No se identifican datos relevantes\.?/g, '- ‚ö™ No se identifican datos relevantes');

  return md.trim();
}

/* ===================== NUEVO: Forzar esquema "Resumen preliminar + vi√±etas" ===================== */
function ensureSectionMarkdown(md, headerText) {
  const headerRe = new RegExp(`^###\\s+${escapeRegex(headerText)}\\s*$`, 'mi');
  if (!headerRe.test(md)) return md;

  // Extrae bloque de la secci√≥n
  const start = md.search(headerRe);
  const rest = md.slice(start);
  const nextHeaderIdx = rest.search(/^\s*###\s+/m);
  const end = nextHeaderIdx > -1 ? start + nextHeaderIdx : md.length;

  const before = md.slice(0, start);
  const block = md.slice(start, end);
  const after = md.slice(end);

  let fixed = block;

  // Asegura l√≠nea de Resumen preliminar
  if (!/\*\*Resumen preliminar:\*\*/i.test(fixed)) {
    fixed = fixed.replace(/^(###[^\n]*\n?)/, `$1\n**Resumen preliminar:** No se identifican datos relevantes\n`);
  }

  // Si no hay vi√±etas, a√±ade la default
  const hasBullet = /(^|\n)-\s+/.test(fixed);
  if (!hasBullet) {
    fixed = fixed.replace(/\*\*Resumen preliminar:\*\*[^\n]*\n?/, (m) => `${m}- ‚ö™ No se identifican datos relevantes\n`);
  }

  // Asegura l√≠nea en blanco entre secciones
  if (!/\n\n$/.test(fixed)) fixed = fixed + '\n';

  return before + fixed + after;
}

function enforceAllSections(md) {
  const sections = [
    'üë• Actores recurrentes',
    'üõ†Ô∏è Vectores de ataque comunes',
    'üè≠ Industrias afectadas',
    'üìâ Impacto general',
    'üìà Tendencias destacadas',
  ];
  let out = md;
  sections.forEach(h => { out = ensureSectionMarkdown(out, h); });
  return out;
}

/* ===================== NUEVO: Resumen general (auto-inyecci√≥n si falta) ===================== */
function injectResumenGeneral(md) {
  if (!md || typeof md !== 'string') return md;
  if (/^####\s*üìù\s*Resumen general\s*$/mi.test(md)) return md; // ya existe

  // Heur√≠stica simple: ¬øhay al menos una vi√±eta que NO sea "‚ö™ ..."?
  const hasFindings = /(^|\n)-\s+(?!‚ö™)/.test(md);
  const summary = hasFindings
    ? 'Panorama con hallazgos en al menos una categor√≠a; priorizar mitigaciones y monitoreo continuo.'
    : 'No se identifican actores, vectores predominantes ni sectores afectados de forma significativa en el periodo analizado.';

  const block = `#### üìù Resumen general
${summary}

`;
  // Inserta al inicio si existe cualquier secci√≥n consolidada
  return block + md;
}

/* ===================== NUEVO: Sanitizador de vi√±etas duplicadas/pegadas ===================== */
function sanitizeBullets(md) {
  if (!md) return md;
  return md
    .replace(/-\s*‚ö™\s*-\s*‚ö™/g, '- ‚ö™')              // "- ‚ö™- ‚ö™" -> "- ‚ö™"
    .replace(/^-{2,}\s+/gm, '- ')                   // "-- " -> "- "
    .replace(/(#### [^\n]+)\s*(?=#### |\Z)/g, '$1\n') // asegura salto tras t√≠tulos
    .replace(/(#### [^\n]+)\s*-\s*‚ö™/g, '$1\n- ‚ö™')   // si vi√±eta pega al t√≠tulo
    ;
}

/* ===================== Markdown -> HTML conversion (reused) ===================== */
function convertMarkdownToHtml(markdown) {
  let html = markdown || '';
  html = html.replace(/^---\s*$/gm, '<hr style="border:0;height:1px;background-color:#e9ecef;margin:30px 0;">');
  html = html.replace(/^(#+)\s*(.*?)$/gm, (match, hashes, titleText) => {
    const hLevel = Math.min(hashes.length, 4);
    const styles = [
      'font-size:32px;color:#0a1f44;font-weight:800;margin:30px 0 12px;padding-bottom:6px;border-bottom:3px solid #0071ce;line-height:1.2;font-family:Segoe UI,Roboto,sans-serif;',
      'font-size:28px;color:#123c69;font-weight:700;margin:26px 0 10px;padding-bottom:4px;border-bottom:2px solid #0071ce;line-height:1.2;',
      'font-size:24px;color:#1a1a1a;font-weight:600;margin:22px 0 8px;padding-bottom:2px;border-bottom:1px solid #ccc;line-height:1.2;',
      'font-size:22px;color:#333;font-weight:600;margin:18px 0 6px;line-height:1.2;',
    ];
    const styleToApply = styles[Math.min(hLevel - 1, styles.length - 1)];
    return `<h${hLevel} style="${styleToApply}">${titleText}</h${hLevel}>`;
  });
  html = html.replace(/\*\*(.*?)\*\*/g, '<strong style="font-weight:bold;color:#041e42;">$1</strong>');
  html = html.replace(/\[(.*?)\]\((.*?)\)/g, '<a href="$2" style="color:#0071ce;text-decoration:none;" target="_blank" rel="noopener noreferrer">$1</a>');
  html = html.replace(/^- (.*?)$/gm, '<li style="margin-bottom:5px;font-size:16px;line-height:1.5;">$1</li>');

  const groupedLines = [];
  let inList = false;
  for (const line of html.split('\n')) {
    if (line.trim().startsWith('<li')) {
      if (!inList) {
        groupedLines.push('<ul style="margin-left:10px;padding-left:20px;margin-bottom:10px;">');
        inList = true;
      }
      groupedLines.push(line);
    } else {
      if (inList) {
        groupedLines.push('</ul>');
        inList = false;
      }
      groupedLines.push(line);
    }
  }
  if (inList) groupedLines.push('</ul>');
  html = groupedLines.join('\n');

  html = html.replace(/((?:^\|.*\|\s*\n?)+)/gm, (match) => {
    const rows = match.trim().split('\n').map((r) => r.trim().split('|').filter((c) => c.trim()));
    const header = rows[0];
    const body = rows.slice(1).filter((r) => !r.every((c) => /^-+$/.test(c)));
    const thead = `<thead><tr>${header.map((h) => `<th style="border:1px solid #ccc;padding:8px 12px;text-align:left;background-color:#0071ce;color:#fff;">${h.trim()}</th>`).join('')}</tr></thead>`;
    const tbody = `<tbody>${body.map((row) => `<tr>${row.map((c) => `<td style="border:1px solid #ccc;padding:8px 12px;text-align:left;">${c.trim()}</td>`).join('')}</tr>`).join('')}</tbody>`;
    return `<table style="border-collapse:collapse;width:100%;margin:20px 0;font-size:14px;" class="markdown-table">${thead}${tbody}</table>`;
  });

  html = html.replace(/\n\n+/g, '</p><p style="margin-bottom:10px;">');
  html = html.replace(/(?<!<\/h[1-6]>|<hr>|<\/ul>|<p>)\n(?!<h[1-6]>|<hr>|<ul>|<p>)/g, '<br/>');
  if (!html.trim().startsWith('<h') && !html.trim().startsWith('<ul') && !html.trim().startsWith('<table') && !html.trim().startsWith('<hr') && !html.trim().startsWith('<p')) {
    html = `<p style="margin-bottom:10px;">${html}</p>`;
  }
  html = html.replace(/<\/h[1-6]><br\/>/g, (m) => m.replace('<br/>','')).replace(/<\/ul><br\/>/g,'</ul>').replace(/<\/p><p style="margin-bottom:10px;">/g,'').replace(/^<p style="margin-bottom:10px;"><\/p>/,'').replace(/<p style="margin-bottom:10px;"><hr style="border:0;height:1px;background-color:#e9ecef;margin:30px 0;"><\/p>/g,'<hr style="border:0;height:1px;background-color:#e9ecef;margin:30px 0;">');

  return html;
}

/* ===================== NUEVO: Resaltar ‚ÄúResumen general‚Äù en HTML ===================== */
function emphasizeResumenGeneral(html) {
  // Envuelve el bloque que sigue al H4 "Resumen general" hasta el siguiente hX en una tarjeta
  return html.replace(
    /(<h4[^>]*>.*?Resumen general.*?<\/h4>)([\s\S]*?)(?=<h[1-6][^>]*>|$)/i,
    (m, h, body) => {
      return `
<div style="background:#f7fbff;border:1px solid #cfe8ff;border-left:5px solid #2b7bff;border-radius:8px;padding:16px 18px;margin:10px 0 18px 0;">
  ${h}
  <div style="font-size:15px;color:#0a1f44;line-height:1.5;">${body.trim()}</div>
</div>`;
    }
  );
}

/* ===================== Template builder ===================== */
function formatMarkdownToHtml(markdown, securityData = null, startDate = null, endDate = null, reportDateISO = null) {
  if (!markdown) markdown = 'No hay contenido para mostrar.';
  const bodyHtmlRaw = convertMarkdownToHtml(markdown);
  const bodyHtml = emphasizeResumenGeneral(bodyHtmlRaw); // resalta Resumen general
  const headerIndicators = computeHeaderIndicators(markdown, securityData);
  const headerIndicatorsHtml = renderHeaderIndicators(headerIndicators);

  const headerHtml = createEmailSafeHeader(startDate, endDate, reportDateISO);
  const footerHtml = createEmailFooter();
  const securitySectionHtml = securityData ? renderSecuritySection(securityData) : '';

  const formattedHTML = `
<!DOCTYPE html>
<html lang="es">
<head>
<meta charset="UTF-8">
<title>Informe de Ciberseguridad</title>
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<style>
  body{font-family:Arial,Helvetica,sans-serif;background-color:#f4f6f8;margin:0;color:#2c3e50;}
  .container{max-width:800px;margin:20px auto;background:#fff;border-radius:8px;box-shadow:0 4px 10px rgba(44,62,80,0.1);overflow:hidden;}
  .content{padding:28px;}
  .panel{background:#fff;border-radius:8px;margin-bottom:20px;}
  .markdown-table th{background-color:#0071ce;color:#fff;}
  .markdown-table tr:nth-child(even) td{background:#f9f9f9;}
  h1,h2,h3,h4{font-family:Segoe UI, Roboto, sans-serif;}
</style>
</head>
<body>
  <div class="container">
    ${headerHtml}
    <div class="content">
      ${headerIndicatorsHtml}
      ${securitySectionHtml ? `<div class="panel">${securitySectionHtml}</div>` : ''}
      <div class="panel">
        ${bodyHtml}
      </div>
    </div>
    ${footerHtml}
  </div>
</body>
</html>`;

  return formattedHTML;
}

/* ===================== EJECUCI√ìN DEL NODO ===================== */
try {
  const raw = $input.first().json.output || $input.first().json.text || $input.first().json;
  const startDate = $input.first().json.startDate || null;
  const endDate = $input.first().json.endDate || null;
  const reportDateISO = formatISO();

  const securityData = tryParseSecurityData(raw);
  let markdownContent = extractMarkdown(raw);

  // Embellecer ‚ÄúAn√°lisis Consolidado‚Äù y forzar esquema
  markdownContent = prettifyConsolidatedSection(markdownContent);
  markdownContent = enforceAllSections(markdownContent);
  markdownContent = injectResumenGeneral(markdownContent);
  markdownContent = sanitizeBullets(markdownContent);

  const finalHtmlOutput = formatMarkdownToHtml(
    markdownContent,
    securityData,
    startDate,
    endDate,
    reportDateISO
  ) || formatMarkdownToHtml(
    'No se encontraron eventos significativos para el periodo analizado.',
    securityData,
    startDate,
    endDate,
    reportDateISO
  );

  return [{ json: { html: finalHtmlOutput } }];
} catch (error) {
  return [{ json: { error: String(error), stack: error && error.stack ? error.stack : undefined } }];
}
