// Function node ‚Äî n8n
// Mapea la respuesta JSON del endpoint, filtra para obtener el ticket m√°s actualizado
// basado en 'case.startdate', y lo formatea para un LLM.
// Incluye una funci√≥n para limpiar HTML y Base64 del campo 'message',
// y una l√≥gica para extraer un mensaje espec√≠fico si el asunto es "FortiSIEM".

// --- Helper Functions ---
function parseDateString(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') return null;
  try {
    const parts = dateStr.match(/(\d{2})\/(\d{2})\/(\d{4})\s(\d{1,2}):(\d{2})\s(AM|PM)/i);
    if (parts) {
      let [, month, day, year, hours, minutes, ampm] = parts;
      hours = parseInt(hours, 10);
      minutes = parseInt(minutes, 10);
      if (ampm && ampm.toUpperCase() === 'PM' && hours < 12) hours += 12;
      if (ampm && ampm.toUpperCase() === 'AM' && hours === 12) hours = 0;
      const date = new Date(year, parseInt(month, 10) - 1, parseInt(day, 10), hours, minutes);
      return isNaN(date.getTime()) ? null : date;
    }
    const d = new Date(dateStr);
    return isNaN(d.getTime()) ? null : d;
  } catch (e) {
    console.error("Error parsing date string:", dateStr, e);
    return null;
  }
}

const getTextValue = (field) => {
  if (field && Array.isArray(field) && field.length > 0 && field[0] && typeof field[0].text === 'string') {
    return field[0].text;
  }
  return '';
};

function cleanHtmlAndBase64(htmlString) {
  if (!htmlString || typeof htmlString !== 'string') return '';
  let cleanedText = htmlString;
  cleanedText = cleanedText.replace(/<[^>]*>/g, '');
  cleanedText = cleanedText.replace(/\b(?:[A-Za-z0-9+/]{4}){8,}(?:[A-Za-z0-9+/]{2}={2}|[A-Za-z0-9+/]{3}=)?\b/g, '[ADJUNTO_REMOVIDO]');
  cleanedText = cleanedText.replace(/data:[^;]+;base64,[A-Za-z0-9+/=]+/g, '[DATA_URI_REMOVIDA]');
  cleanedText = cleanedText.replace(/(\s*\r?\n\s*){2,}/g, '\n'); 
  cleanedText = cleanedText.replace(/[ \t]+/g, ' ');
  return cleanedText.trim();
}

// --- Main Script ---
const items = $input.all();
const allTickets = [];

const rawTicketsData = items[0].json.data; 
const ticketsToProcess = Array.isArray(rawTicketsData) ? rawTicketsData : (rawTicketsData ? [rawTicketsData] : []);

const FORTISIEM_MESSAGE_START = "The following Incident has triggered for the first time.";

for (const itemData of ticketsToProcess) {
  const record = itemData.values; 

  if (!record || typeof record !== 'object') {
    console.warn("Skipping invalid itemData, no 'values' object found:", itemData);
    continue;
  }

  const fechaCreacionRaw = record['case.createddate'];
  const fechaIncidenteRaw = record['case.startdate'];
  const ticketSubject = record['subject'] || ''; // Capturamos el asunto para la l√≥gica
  let originalMessage = record['message'] || ''; // Capturamos el mensaje original

  const fechaCreacionDate = parseDateString(fechaCreacionRaw);
  const fechaIncidenteDate = parseDateString(fechaIncidenteRaw);

  let finalMessageForTicket = cleanHtmlAndBase64(originalMessage); // Limpiamos el mensaje por defecto

  // === L√≥gica para FortiSIEM ===
  if (ticketSubject.includes("FortiSIEM")) {
      const startIndex = finalMessageForTicket.indexOf(FORTISIEM_MESSAGE_START);
      if (startIndex !== -1) {
          // Si encontramos el inicio, tomamos el mensaje desde all√≠
          // y limpiamos nuevamente por si hay algo m√°s despu√©s
          finalMessageForTicket = cleanHtmlAndBase64(finalMessageForTicket.substring(startIndex));
      } else {
          // Si es FortiSIEM pero no tiene el texto esperado, 
          // podr√≠amos dejar un mensaje por defecto o el mensaje limpio original.
          // Por ahora, lo dejaremos como el mensaje limpio original.
          // Opcional: podr√≠as poner aqu√≠ un mensaje como "Mensaje de FortiSIEM con formato inesperado."
      }
  }


  const mappedTicket = {
    "Numero de tk": record['case.casenumber'] || 'N/A',
    "Estado": getTextValue(record['case.status']) || 'N/A',
    "Creador": getTextValue(record['case.custevent_ifx_ticketowner']) || 'N/A',
    "Tipo": getTextValue(record['case.category']) || 'N/A',
    "Asunto": ticketSubject || 'N/A', // Usamos el asunto capturado
    "Responsable": getTextValue(record['case.custevent_ifx_directresponsible']) || 'N/A',
    "Fecha de creaci√≥n": fechaCreacionRaw || 'N/A',
    "Fecha de creaci√≥n (Date)": fechaCreacionDate,
    "Fecha del incidente/Solicitud": fechaIncidenteRaw || 'N/A',
    "Fecha del incidente/Solicitud (Date)": fechaIncidenteDate,
    "Cliente": record['customer.entityid'] || 'N/A',
    "Origen Cliente": record['customer.custentity_ifx_origin'] || 'N/A', 
    "Perfil": record['customer.custentity_ifx_profile'] || 'N/A',
    "Subsidiaria": record['customer.custentity_ifx_subsidiary'] || 'N/A',
    "Cliente ID": record['customer.custentity_ifx_id_quore'] || 'N/A',
    "Tipo de Cliente": getTextValue(record['customer.custentity_ifx_customer_type']) || 'N/A',
    "Estado Cliente": record['customer.custentity_ifx_customer_status'] || 'N/A',
    "Service Manager": getTextValue(record['customer.custentity_ifx_servicemanager']) || 'N/A',
    "Contacto": record['customer.custentity_ifx_contact'] || 'N/A',
    "Mensaje": finalMessageForTicket // <-- Usamos el mensaje procesado
  };
  allTickets.push(mappedTicket);
}

// Ordenar todos los tickets por "Fecha del incidente/Solicitud" de forma descendente
allTickets.sort((a, b) => {
  const dateA = a["Fecha del incidente/Solicitud (Date)"];
  const dateB = b["Fecha del incidente/Solicitud (Date)"];
  if (!dateA && !dateB) return 0;
  if (!dateA) return 1;
  if (!dateB) return -1;
  return dateB.getTime() - dateA.getTime();
});

const mostRecentTicket = allTickets.length > 0 ? allTickets[0] : null;

// === Formato para LLM ===
let summaryForLLM = "";

if (mostRecentTicket) {
  delete mostRecentTicket["Fecha de creaci√≥n (Date)"];
  delete mostRecentTicket["Fecha del incidente/Solicitud (Date)"];

  summaryForLLM += "--- Ticket M√°s Actualizado ---\n";
  summaryForLLM += `N√∫mero: ${mostRecentTicket["Numero de tk"]}\n`;
  summaryForLLM += `Estado: ${mostRecentTicket["Estado"]}\n`;
  summaryForLLM += `Asunto: ${mostRecentTicket["Asunto"]}\n`;
  summaryForLLM += `Tipo: ${mostRecentTicket["Tipo"]}\n`;
  summaryForLLM += `Fecha Creaci√≥n: ${mostRecentTicket["Fecha de creaci√≥n"]}\n`;
  summaryForLLM += `Fecha Incidente/Solicitud: ${mostRecentTicket["Fecha del incidente/Solicitud"]}\n`;
  summaryForLLM += `Responsable: ${mostRecentTicket["Responsable"]}\n`;
  summaryForLLM += `Cliente: ${mostRecentTicket["Cliente"]}\n`;
  summaryForLLM += `Tipo de Cliente: ${mostRecentTicket["Tipo de Cliente"]}\n`;
  summaryForLLM += `Service Manager: ${mostRecentTicket["Service Manager"]}\n`;
  summaryForLLM += `Mensaje: ${mostRecentTicket["Mensaje"]}\n\n`;

  summaryForLLM += "Este es el ticket m√°s reciente basado en la 'Fecha del incidente/Solicitud'.";
} else {
  summaryForLLM = "No se encontraron tickets v√°lidos para procesar.";
}

return [{ json: { summary: summaryForLLM } }];

const items = [];

// FUNCI√ìN CORREGIDA: Extremadamente robusta para el patr√≥n * **Etiqueta:** Valor
const extractValue = (section, label) => {
    if (typeof section !== 'string' || !section) return 'N/A';

    // Escapa caracteres especiales de regex en la etiqueta (ej. N¬∞ -> N\¬∞)
    const escapedLabel = label.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');

    // RegEx FINAL: Busca el patr√≥n * **Etiqueta:** Valor en cualquier parte de la secci√≥n.
    const regex = new RegExp(`\\*\\s*\\*\\*?${escapedLabel}\\*\\*?\\s*:\\s*([^\\n]+)`, 'i');

    const match = section.match(regex);

    return (match && match[1]) ? match[1].trim() : 'N/A';
};

// Funci√≥n para extraer la descripci√≥n
const extractDescription = (section) => {
    if (typeof section !== 'string' || !section) return 'Descripci√≥n no disponible.';
    // Busca "Descripci√≥n del Evento:" y captura hasta el siguiente encabezado Markdown o el final.
    const regex = /Descripci√≥n del Evento:\s*([\s\S]*?)(?=\*\*Caracter√≠sticas del Evento\*\*|N\/A|An√°lisis T√©cnico)/i;
    const match = section.match(regex);
    return (match && match[1]) ? match[1].trim().replace(/\n/g, ' ') : 'Descripci√≥n no disponible.';
};


for (const item of $input.all()) {
    const rawOutput = item.json.output || "";

    // --- 1. PROCESAMIENTO: Dividir el texto en secciones ---
    const sections = {
        info: rawOutput.match(/### Informaci√≥n del Cliente y Evento([\s\S]*?)###/i),
        details: rawOutput.match(/### Detalles del Evento([\s\S]*?)###/i),
        // La secci√≥n de descripci√≥n es la √∫ltima, capturamos todo lo que queda
        desc: rawOutput.match(/### Descripci√≥n y Recomendaciones([\s\S]*)/i)
    };

    // Usamos el grupo de captura [1] para obtener solo el texto DESPU√âS del encabezado ###
    const infoSection = sections.info ? sections.info[1] : '';
    const detailsSection = sections.details ? sections.details[1] : '';
    // Para la secci√≥n final (desc), usamos [0] (el bloque completo) para las RegEx internas de listas.
    const descBlock = sections.desc ? sections.desc[0] : '';

    // --- 2. EXTRACCI√ìN de Datos Clave al objeto 'data' ---

    const data = {
        // Info Cliente y Evento
        'N¬∞ Ticket': extractValue(infoSection, 'N¬∫ Ticket'),
        'Severidad': extractValue(infoSection, 'Severidad'),
        'Fecha del Evento': extractValue(infoSection, 'Fecha del Evento'),
        'Cliente Afectado': extractValue(infoSection, 'Cliente Afectado'),
        'Analista': extractValue(infoSection, 'Analista'),
        // Se asume que el LLM genera la Categor√≠a correctamente con * **Categor√≠a:** Valor
        'Categor√≠a': extractValue(infoSection, 'Categor√≠a'),
        'Regla Aplicada': extractValue(infoSection, 'Regla Aplicada'),

        // Detalles del Evento
        'IP de Origen': extractValue(detailsSection, 'IP de Origen'),
        'Usuario que Realiz√≥ la Acci√≥n': extractValue(detailsSection, 'Usuario que Realiz√≥ la Acci√≥n'),
        'Hostname': extractValue(detailsSection, 'Hostname'),
        'Dominio': extractValue(detailsSection, 'Dominio'),
        'EventID': extractValue(detailsSection, 'EventID'),

        // Descripci√≥n y An√°lisis (USANDO LA L√çNEA MODIFICADA AQU√ç)
        'Descripci√≥n del Evento': extractDescription(descBlock),
        'Nivel de riesgo': extractValue(descBlock, 'Nivel de riesgo'), // <--- CAMBIO CLAVE APLICADO

        // Extraer Listas
        Factores_Array: (descBlock.match(/Factores que contribuyen a esta evaluaci√≥n:([\s\S]*?)RECOMENDACIONES/i) || [''])[0]
            .split('\n')
            .filter(line => line.includes('+'))
            .map(line => line.replace(/\+/g, '').trim().replace(/\.$/, '')),

        Recomendaciones_Array: (descBlock.match(/RECOMENDACIONES:([\s\S]*)/i) || [''])[0]
            .split('\n')
            .filter(line => line.includes('*') && !line.includes('Security ID'))
            .map(line => line.replace(/\*/g, '').trim().replace(/\.$/, ''))
            .filter(line => line.length > 5 && !line.includes('RECOMENDACIONES'))
    };

    // --- 3. VARIABLES SEGURAS PARA CLASES CSS ---
    const severity = data.Severidad || "N/A";
    const severityClass = severity.split(" ")[0].toLowerCase();
    const riskLevel = data["Nivel de riesgo"] || "N/A";
    // Mapeo de Nivel de riesgo: Medio -> medio, Alto -> alto, etc.
    const riskClass = riskLevel.split(" ")[0].toLowerCase().replace(':', '');


    // --- 4. GENERACI√ìN DEL HTML (Se mantiene sin cambios) ---
    const htmlContent = `
<div class="report-container">
    <style>
        /* INICIO: Estilos CSS (omitidos por brevedad) */
        .report-container { /* ... */ }
        .card { /* ... */ }
        /* ... */
        .risk-${riskClass} { 
            font-weight: bold;
            color: ${riskClass === 'medio' ? '#ff8a00' : (riskClass === 'alto' ? '#dc3545' : '#007bff')}; 
            background-color: ${riskClass === 'medio' ? '#fff0db' : (riskClass === 'alto' ? '#fcebeb' : '#e6f7ff')};
            padding: 4px 10px;
            border-radius: 5px;
            display: inline-block;
        }
        /* ... */
    </style>

    <div class="card card-summary">
        <h2 class="card-title">üö® Evento de Seguridad - Resumen</h2>
        <div class="summary-grid">
            <div class="data-item"><span class="label">Ticket:</span> <span class="value ticket-number">${data["N¬∞ Ticket"]}</span></div>
            <div class="data-item"><span class="label">Severidad:</span> <span class="value severity severity-${severityClass}">${severity}</span></div>
            <div class="data-item"><span class="label">Fecha/Hora:</span> <span class="value">${data["Fecha del Evento"]}</span></div>
            <div class="data-item"><span class="label">Cliente Afectado:</span> <span class="value">${data["Cliente Afectado"]}</span></div>
            <div class="data-item"><span class="label">Analista:</span> <span class="value">${data.Analista}</span></div>
            <div class="data-item"><span class="label">Categor√≠a:</span> <span class="value">${data.Categor√≠a}</span></div>
        </div>
    </div>

    <div class="card card-details">
        <h3 class="card-title">Detalles T√©cnicos del Acceso</h3>
        <p class="description-text"><strong>Descripci√≥n:</strong> ${data["Descripci√≥n del Evento"]}</p>
        <hr>
        <div class="detail-columns">
            <div class="data-item"><span class="label">Regla Aplicada:</span> <span class="value">${data["Regla Aplicada"]}</span></div>
            <div class="data-item"><span class="label">Event ID:</span> <span class="value">${data.EventID}</span></div>
            <div class="data-item"><span class="label">IP de Origen:</span> <span class="value ip-address">${data["IP de Origen"]}</span></div>
            <div class="data-item"><span class="label">Usuario:</span> <span class="value">${data["Usuario que Realiz√≥ la Acci√≥n"]}</span></div>
            <div class="data-item"><span class="label">Hostname:</span> <span class="value">${data.Hostname}</span></div>
            <div class="data-item"><span class="label">Dominio:</span> <span class="value">${data.Dominio}</span></div>
        </div>
    </div>

    <div class="card card-analysis">
        <h3 class="card-title">An√°lisis de Riesgo y Acciones</h3>

        <div class="risk-section data-item">
            <span class="label">Nivel de Riesgo:</span> 
            <span class="value risk-level risk-${riskClass}">
                ${riskLevel}
            </span>
        </div>

        <hr>

        <h4>Factores de Riesgo:</h4>
        <ul class="factors-list">
            ${data.Factores_Array.map(f => `<li>${f}</li>`).join('')}
        </ul>

        <h4>Recomendaciones:</h4>
        <ol class="recommendations-list">
            ${data.Recomendaciones_Array.map(r => `<li>${r}</li>`).join('')}
        </ol>
    </div>
</div>
    `;

    // 5. Devolver SOLO el objeto con el HTML
    items.push({ htmlContent: htmlContent });
}

return items;
